function Metodo_de_Jacobi  clc;  warning('off', 'all');;  a=[4,-2,1;20,-7,12;-8,13,17];  b=[11;70;17];  iterMax=2500;  tol=1e-10;  [xk]=jacobi(a,b,tol,iterMax);    if xk!=inf    xk=xk';    xk_str=[];    for i=1:size(xk,2)        xk_str=[xk_str "x" num2str(i) "=" num2str(xk(i)) " "];    endfor    disp(["Las soluciones del sistema de ecuaciones son: "]);    disp(xk_str);  endif  endfunction [xk,error,k]=jacobi(a,b,tol,iterMax)  n=size(b,1);  d=diag(diag(a));%diag de diag(a) por que si se hace solo una vez me estaria ebtregando un vector con la daigonal de a, no la matriz identidad como tal  lpu=a-d;  d_inv=diag(1./diag(d));%Forma de calcular al inversa de una matriz  t=-d_inv*lpu;  c=d_inv*b;  xk=zeros(n,1);  error=0;  for k=1:iterMax    xk=t*xk+c;    error=norm(a*xk-b);%norma de tipo 2 por default    if abs(error)<=tol      error=abs(error);      return    endif  endfor  error=abs(error);  returnend 